import React, { useEffect, useState } from "react";
import {
  DataGrid,
  GridColDef,
  GridPaginationModel,
  GridValueFormatter,
} from "@mui/x-data-grid";
import { getPersonas } from "../services/api";

interface Persona {
  id?: number;
  carnet: string;
  nombres: string;
  apellidos: string;
  sexo: string;
  fechaNacimiento: string | Date;
  profesion: string;
  celular: string;
  direccion: string;
  fechaRegistro?: string | Date;
}

const columns: GridColDef<Persona>[] = [
  { field: "carnet", headerName: "Carnet", width: 120 },
  { field: "nombres", headerName: "Nombres", width: 150 },
  { field: "apellidos", headerName: "Apellidos", width: 150 },
  { field: "sexo", headerName: "Sexo", width: 100 },
  {
    field: "fechaNacimiento",
    headerName: "Fecha Nac.",
    width: 120,
    valueFormatter: (params: GridValueFormatterParams<string | Date>) => {
      if (!params.value) return "";
      try {
        return new Date(params.value).toLocaleDateString();
      } catch {
        return params.value.toString();
      }
    },
  },
  { field: "profesion", headerName: "Profesi贸n", width: 150 },
  { field: "celular", headerName: "Celular", width: 120 },
  { field: "direccion", headerName: "Direcci贸n", width: 200 },
];

export const PersonaTable: React.FC = () => {
  const [personas, setPersonas] = useState<Persona[]>([]);
  const [loading, setLoading] = useState(true);
  const [paginationModel, setPaginationModel] = useState<GridPaginationModel>({
    page: 0,
    pageSize: 5,
  });

  const fetchPersonas = async () => {
    try {
      setLoading(true);
      const response = await getPersonas();
      setPersonas(Array.isArray(response?.data) ? response.data : []);
    } catch (error) {
      console.error("Error al obtener personas:", error);
      setPersonas([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPersonas();
  }, []);

  return (
    <div style={{ height: 400, width: "100%" }}>
      <DataGrid
        rows={personas}
        columns={columns}
        paginationModel={paginationModel}
        onPaginationModelChange={setPaginationModel}
        pageSizeOptions={[5, 10, 25]}
        loading={loading}
        getRowId={(row) => row.carnet}
      />
    </div>
  );
};
